mapper.select:
SELECT l.id AS "id", l.type AS "type", l.title AS "title", l.remote_ip AS "remoteIp", l.user_agent AS "userAgent", l.request_uri AS "requestUri", l.method AS "method", l.params AS "params", l.exception AS "exception", l.create_by AS "createBy.id", l.create_time AS "createTime", u.name AS "createBy.name" FROM sys_log l JOIN sys_user u ON u.id = a.create_by
AND l.create_time gt;= #{beginDate}
AND l.create_time lt;= #{endDate}
AND l.title LIKE CONCAT('%', #{title}, '%')
AND l.create_by = #{createBy.id}
AND l.request_uri LIKE CONCAT('%', #{requestUri}, '%')
AND l.type = #{TYPE_EXCEPTION}
ORDER BY l.create_time DESC
