mapper.select:
SELECT * FROM sys_dict WHERE del_flag = #{NORMAL}
AND type = #{type}
AND description LIKE CONCAT('%', #{description}, '%')
ORDER BY type, sort, update_date DESC

mapper.update:
UPDATE sys_dict SET 
	value = #{value}, 
	label = #{label}, 
	type = #{type}, 
	description = #{description}, 
	sort = #{sort},
	update_by = #{updateBy.id}
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE sys_dict SET 
	del_flag = #{DELETE}
WHERE id = #{id}
